(window.webpackJsonp=window.webpackJsonp||[]).push([[13],{198:function(t,a,s){"use strict";s.r(a);var r=s(6),e=Object(r.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第一课-中国古代政治制度的形成与发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第一课-中国古代政治制度的形成与发展"}},[t._v("#")]),t._v(" 第一课 中国古代政治制度的形成与发展")]),t._v(" "),a("h2",{attrs:{id:"一、先秦时期的政治制度"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、先秦时期的政治制度"}},[t._v("#")]),t._v(" 一、先秦时期的政治制度")]),t._v(" "),a("p",[t._v("公元前2070年，禹建立了我国最早的奴隶制王朝国家夏。禹死后，其子启继位，世袭制代替了禅让制。商朝建立后，实行内外服制，商王直接控制内服王畿地区，王畿四周是外服。")]),t._v(" "),a("p",[t._v("西周时期，天子是国家最高统治者，控制王畿地区，其他地方分封给同姓贵族、异姓功臣或旧贵族，在各地建立诸侯国。诸侯在国内又分封卿大夫，卿大夫再分封士，形成贵族等级分封序列。按照宗法制，周天子与各级贵族都实行嫡长子继承制。分封制与宗法制相配合，政治权力分配与血缘关系相结合，是西周政体的基本特征。")]),t._v(" "),a("p",[t._v("商周政体中，君主的权力不是绝对的，其中的原始民主传统，对君主的权力有制约作用。“防民之口，甚于防川”这些原始民主传统，在春秋各国政治中仍然有一定影响。春秋时期，随着社会经济的发展和宗法血缘关系的瓦解，贵族等级分封制开始解体。战国时期，为适应社会变化的需要，各国在政治上进行了重大改革，君主权力加强，郡县制、官僚制等封建政治制度开始产生。")]),t._v(" "),a("h2",{attrs:{id:"二、秦朝的政治制度"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、秦朝的政治制度"}},[t._v("#")]),t._v(" 二、秦朝的政治制度")]),t._v(" "),a("p",[t._v("秦的统一结束了长期战争的混乱局面，在全国范围内建立起中央集权的政治制度。皇帝自称“朕”，命和令分别称为“制”与“诏”，印称为“玺”。皇帝独尊，其位世袭，皇权至上。秦朝以公卿为首，下设三公九卿。三公指丞相、太尉、御史大夫。丞相府是行政中枢；太尉掌军事；御史大夫是掌管图籍、文书，同时又是最高监察官。在秦朝统一的中央集权国家治理中，文书发挥着重要作用，各级官僚机构以文书推行行政管理。秦朝还建立了以邮传为中心的文书传送系统，以保障皇帝和中央的政令能够传送到全国各地，保障了政令的准确传达，提高行政效率，加强了中央集权，为汉承秦制提供了条件。")]),t._v(" "),a("h2",{attrs:{id:"三、两汉至明清时期政治制度的演变"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#三、两汉至明清时期政治制度的演变"}},[t._v("#")]),t._v(" 三、两汉至明清时期政治制度的演变")]),t._v(" "),a("p",[t._v("汉朝地方行政制度沿袭秦朝的郡、县二级制。郡设太守为一郡最高长官，其下设郡尉、郡丞，地方层面有分权存在。汉初地方行政制度中还包括王国和侯国。东汉晚期，原来的监察区“州”变为一级行政机构，形成州、郡、县三级制。隋朝废郡行州、县二级制，唐朝沿用。唐朝根据山川形势把全国划分为10道，后增至15道，作为中央派出的监察机构。明初废行中书省，但省的格局并未改变，原行中书省的权力由布政使司、按察使司、都指挥使司分割。清承明制，巡抚成了一省长官，又设总督掌管一省或数省军政大权，形成省、府、县三级行政制度。")])])}),[],!1,null,null,null);a.default=e.exports}}]);