(window.webpackJsonp=window.webpackJsonp||[]).push([[14],{199:function(t,a,s){"use strict";s.r(a);var r=s(6),e=Object(r.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第十课-当代中国的法治与精神文明建设"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第十课-当代中国的法治与精神文明建设"}},[t._v("#")]),t._v(" 第十课 当代中国的法治与精神文明建设")]),t._v(" "),a("h2",{attrs:{id:"一、新中国的法治建设进程"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、新中国的法治建设进程"}},[t._v("#")]),t._v(" 一、新中国的法治建设进程")]),t._v(" "),a("p",[t._v("1949年9月的中国人民政治协商会议通过了《共同纲领》，是中华人民共和国法治建设历程的开始。社会主义法制是社会主义国家实施的体现广大人民意志的法律制度。50年代初创时期，我国制定了《婚姻法》《土地改革法》等法律法令。1954年《中华人民共和国宪法》确立了社会主义中国的政治制度、立法制度、司法制度，初步奠定了中国法治建设的基础。文化大革命时期，社会主义法制遭到严重破坏。")]),t._v(" "),a("p",[t._v("改革开放后，为做到有法可依、有法必依、执法必严、违法必究，保障法律面前人人平等，1982年通过《中华人民共和国宪法》，我国还制定了《刑法》《刑事诉讼法》《中外合资经营企业法》《经济合同法》等。90年代我国对法治建设提出了更高的要求，中共十五大提出要“依法治国，建设社会主义法治国家”，将“法制”改为“法治”。1999年和2004年，《宪法修正案》先后将依法治国和尊重保障人权写入宪法，法治建设得到进一步加强。2010年，我国形成了中国特色社会主义法律体系，为实现中华民族伟大复兴奠定了坚实的法制基础。")]),t._v(" "),a("p",[t._v("十八大以来，我国切实贯彻落实科学立法、严格执法、公正司法、全民守法的依法治国方针，推进国家治理体系和治理能力现代化，全面依法治国进入一个新阶段。2018年《宪法修正案》把十九大确定的习近平新时代中国特色社会主义思想载入国家根本法，2020年5月通过了《中华人民共和国民法典》，是新中国第一部以法典为名的法律，在法律体系中居于基础性地位，被称为“社会生活的百科全书”，是一部具有鲜明中国特色、世界特色、时代特色的民法典。")]),t._v(" "),a("h2",{attrs:{id:"二、社会主义精神文明建设"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、社会主义精神文明建设"}},[t._v("#")]),t._v(" 二、社会主义精神文明建设")]),t._v(" "),a("p",[t._v("在社会主义革命和建设时期，中国人民发扬英勇奋斗的革命传统和艰苦奋斗的精神，孟泰、时传祥，铁人王进喜，党的好干部焦裕禄，解放军好战士雷锋，科学家李四光、华罗庚等是杰出代表。全社会形成了健康向上的道德风尚，热爱党、热爱社会主义的政治氛围，关心集体、无私奉献、全心全意为人民服务的行动准则，互相关心、互相爱护、互相帮助的新型人际关系。改革开放后，“五讲四美三热爱”是20世纪80年代群众性精神文明建设最响亮的口号90年代我国又开展文明城市，文明村镇、文明行业系列创建活动， 1994年，中共中央把爱国主义教育作为加强精神文明建设的基础工程加以推进。2001年颁布的《公民道德建设实施纲要》从以德治国的高度进一步规划思想道德建设。")]),t._v(" "),a("p",[t._v("2006年10月第一次提出建设社会主义核心价值体系的战略任务。中共十七大提出“社会主义核心价值体系是社会主义意识形态的本质体现”，中共十八大提炼的社会主义核心价值观，是当代中国精神的集中体现，凝结着全体人民共同的价值追求，涌现出杨善洲、黄大年等一大批道德模范。")])])}),[],!1,null,null,null);a.default=e.exports}}]);