(window.webpackJsonp=window.webpackJsonp||[]).push([[18],{203:function(t,a,s){"use strict";s.r(a);var r=s(6),e=Object(r.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第十三课-当代中国的民族政策"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第十三课-当代中国的民族政策"}},[t._v("#")]),t._v(" 第十三课 当代中国的民族政策")]),t._v(" "),a("h2",{attrs:{id:"一、民族区域自治制度的建立"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、民族区域自治制度的建立"}},[t._v("#")]),t._v(" 一、民族区域自治制度的建立")]),t._v(" "),a("p",[t._v("1941年，陕甘宁边区政府规定“建立蒙、回民族自治区”，1945年中共中央提出：“对内蒙的基本方针，在目前是实行区域自治。”1947年5月1日，经中共中央批准，在刚刚解放的内蒙古地区成立内蒙古自治区，新中国成立后，它成为我国第一个省级自治区。1949年《共同纲领》规定，中华人民共和国境内各民族一律平等，实行团结互助。1954年宪法规定各民族自治地方是中华人民共和国不可分割的部分，各自治机关在国家统一领导下行使自治权。民族区域自治制度被确立为我国的一项基本政治制度。1965年，西藏自治区成立。")]),t._v(" "),a("h2",{attrs:{id:"二、民族区域自治制度的发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、民族区域自治制度的发展"}},[t._v("#")]),t._v(" 二、民族区域自治制度的发展")]),t._v(" "),a("p",[t._v("1984年《中华人民共和国区域自治法》颁布实施，以基本法律的形式把民族区域自治制度固定下来。1990年，中共中央提出“三个离不开”，即“汉族离不开少数民族，少数民族离不开汉族，少数民族之间也互相离不开”，深刻阐述了中国各民族休戚相关、命运与共的血肉关系。1997 年，中共十五大明确把民族区域自治制度确立为建设有中国特色社会主义政治的基本政治制度之一。民族区域自治制度的优势在于维护国家的集中统一，保障少数民族合法权益，巩固和发展平等团结互助和谐的社会主义民族关系，打牢中华民族共同体思想基础，实现民族地区经济社会事业加快发展，充分尊重了少数民族宗教信仰和风俗习惯，是实现少数民族人民当家作主的重要途径。")]),t._v(" "),a("h2",{attrs:{id:"三、中共十八大以来民族区域自治制度的完善"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#三、中共十八大以来民族区域自治制度的完善"}},[t._v("#")]),t._v(" 三、中共十八大以来民族区域自治制度的完善")]),t._v(" "),a("p",[t._v("中共十八大以来，党和国家要求坚持各民族“共同团结奋斗，共同繁荣发展”的民族工作主题，不断增进各族群众对伟大祖国、中华民族、中华文化、中国共产党和中国特色社会主义的认同。中共十九大报告提出，全面贯彻党的民族政策，深化民族团结进步教育，铸牢中华民族共同体意识被写入新修订的《中国共产党章程》，赋予民族工作新的内涵和重大历史使命，是习近平新时代中国特色社会主义思想在民族工作领域的具体体现。")])])}),[],!1,null,null,null);a.default=e.exports}}]);