(window.webpackJsonp=window.webpackJsonp||[]).push([[21],{206:function(t,a,s){"use strict";s.r(a);var r=s(6),e=Object(r.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第十七课-中国古代的户籍制度与社会治理"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第十七课-中国古代的户籍制度与社会治理"}},[t._v("#")]),t._v(" 第十七课 中国古代的户籍制度与社会治理")]),t._v(" "),a("h2",{attrs:{id:"一、历代户籍制度演变"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、历代户籍制度演变"}},[t._v("#")]),t._v(" 一、历代户籍制度演变")]),t._v(" "),a("p",[t._v("制定户籍的做法始于战国时期，秦献公以五家为“伍”的方法编排户口；秦朝的户籍实行分类登记制度，除了百姓户籍还有宗亲贵族的宗室籍、官吏的宦籍、商贾的市籍等。汉朝采取编户齐民政策，隋朝建立后命州县官“大索貌阅”，唐承隋制，管理更严，户籍三年一造。东晋时期，对南方土著居民以黄籍登记，北方南渡而来的人口以白籍登记，不向白籍人口征发赋役。东晋后期政府为增加赋役，不时将侨居户口编入所居住的郡县户籍，称作“土断”使得白籍人口土著化，承担赋役。宋朝时期，户籍分为主户与客户。主户指拥有土地缴纳赋税的税户，客户指没有土地的佃户。元朝统称为“诸色户计”，一旦定籍，世代相袭，不可变动。明朝户籍册称作“黄册”以里甲制为基础，清朝的户籍管理则相对松弛，实行固定丁银、摊丁入亩后，户籍作用大幅削弱。乾隆年间，朝廷谕令户籍永停编审，此后政府只是按照一定的组织制度登记人口数量。")]),t._v(" "),a("h2",{attrs:{id:"二、历代基层组织与社会治理"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、历代基层组织与社会治理"}},[t._v("#")]),t._v(" 二、历代基层组织与社会治理")]),t._v(" "),a("p",[t._v("从秦汉到明清，县是最基层的行政机构，下设直接管理民众的基层组织。秦汉时期，县下设乡和里。乡设三老，掌教化；设啬夫，掌狱讼、赋税；设游徼，掌捕盗。里设里正。乡、里之外有亭，设亭长。唐朝以百户为里，五里为乡，城内设坊，郊外设村，设里正、坊正、村正。明朝实行里甲制，十户为一甲，一百一十户为一里，注重建立基层民众的自我管理与相互监督机制。秦汉时期实行什伍组织，唐朝实行邻保制度，北宋王安石实施保甲制，明朝王守仁推行十家牌法，清初实行里甲制，后来改而推行保甲制。至此，兼具区划和户籍管理性质的乡里制与旨在维护社会治安的保甲制合一。")]),t._v(" "),a("h2",{attrs:{id:"三、历代社会救济与优抚政策"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#三、历代社会救济与优抚政策"}},[t._v("#")]),t._v(" 三、历代社会救济与优抚政策")]),t._v(" "),a("p",[t._v("社会救济为民众提供一定的生活保障，以保证人口繁衍和正常生产活动的进行，有利于维护统治。历代社会救济的主体是掌握大量资源的政府，民间组织处于辅助地位。汉朝建立常平仓制度，积谷备仓，调节粮价。隋唐时期，政府既重视官方储备，也大力提倡民间积储，鼓励民间自置义仓。后世社仓、义仓的设置较为普遍。")]),t._v(" "),a("p",[t._v("中国古代一直有优抚老弱贫苦等弱势群体的传统。秦汉时期，皇帝有时会赐给高龄老人手杖——鸠杖，以示尊重。明初朝廷令各地有司优抚高年平民，八十岁以上月给米五斗、酒三斗、肉五斤。从唐朝开始，政府设有收容贫老、孤儿和乞讨流浪人员的专门机构，如唐朝的养病坊、宋朝的福田院、元朝的众济院、明清的养济院。宋朝以后，宗族内部的救助活动逐渐兴起。北宋范仲淹在族内创设义田，宗族设立义田、义学、义宅、义冢等族产。明清时期，慈善组织开始兴起，出现了善堂、善会等慈善机构。在古代社会，政府救济的重点在救灾，核心在于保证粮食供应，社会力量的救济活动侧重于日常生活中的赈济。")])])}),[],!1,null,null,null);a.default=e.exports}}]);