(window.webpackJsonp=window.webpackJsonp||[]).push([[21],{206:function(t,s,v){"use strict";v.r(s);var _=v(6),a=Object(_.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"第十八课-世界主要国家的基层治理与社会保障"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#第十八课-世界主要国家的基层治理与社会保障"}},[t._v("#")]),t._v(" 第十八课 世界主要国家的基层治理与社会保障")]),t._v(" "),s("h2",{attrs:{id:"一、西方主要国家基层治理的历史与特点"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#一、西方主要国家基层治理的历史与特点"}},[t._v("#")]),t._v(" 一、西方主要国家基层治理的历史与特点")]),t._v(" "),s("p",[t._v("古希腊时期，基层治理依靠村社进行。村社实行自治，全体成年男性出席的村社大会是村社最高权力机关，处理与村社有关的事务。西欧封建社会中，庄园是主要的基层单位，庄园主或管家管理庄园事务。他们组织生产，征收地租，并主持庄园法庭的审判，裁决纠纷。10—11世纪，西欧城市兴起，享有不同程度的自治。城市里的手工业者和商人组成行会或商会，其上层分子把持城市政权。同时，基督教会在基层治理中也发挥了重要作用。在近代西欧民族国家中，英国确立了自治市制度；美国建国后将乡镇定为最基本的地方自治单位；法国大革命后，形成了以自治市镇为基层单位的制度，市长同时对中央政府和本地选民负责。随着工业革命的发展，城市人口激增，失业、贫困等社会问题使传统的社会救济方式遭遇瓶颈。")]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("基层治理的特点")]),t._v(" "),s("p",[t._v("①自治明显，自主权逐步扩大")]),t._v(" "),s("p",[t._v("②逐步完善，循序渐进")]),t._v(" "),s("p",[t._v("③带有民主色彩（独立性）")]),t._v(" "),s("p",[t._v("④趋于规范化法治化（联合国推动作用）")]),t._v(" "),s("p",[t._v("1995年《通过社区发展促进社会进步的报告》")])]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("社区组织开始形成的原因")]),t._v(" "),s("p",[t._v("①工业革命的扩展")]),t._v(" "),s("p",[t._v("②城市人口激增，社会问题突出")]),t._v(" "),s("p",[t._v("③传统的社会救济遇到瓶颈")])]),t._v(" "),s("p",[t._v("第二次世界大战后，社区很快发展为基层自治的主要方式。社区实行居民自我管理，不仅提供生活服务、教育、休闲娱乐、福利、卫生保健等，还参与相关的城市规划、土地政策等地方政府的决策，缓和了社会矛盾，维护了社会秩序和政治稳定。20世纪80年代以后，西方国家的基层治理更加强调政府、社区和非政府组织的共同作用，社区承担了更多的政府功能，使基层治理的效率更高、成本更低。")]),t._v(" "),s("h2",{attrs:{id:"二、现代社会保障制度的建立与发展"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#二、现代社会保障制度的建立与发展"}},[t._v("#")]),t._v(" 二、现代社会保障制度的建立与发展")]),t._v(" "),s("p",[t._v("由于圈地运动致使农民无地可种，大量涌入城市成为乞丐，1601年，英国颁布了济贫法，这是世界最早的社会保障法。德国1871年统一后，随着工业革命以及工人运动的深入发展，初步建立了社会保险制度，涉及疾病、工伤和养老等方面。1929-1933年经济危机期间，罗斯福实行新政用国家干预经济，且美国处于霸主地位。1935 年，美国颁布《社会保障法》，标志着美国现代社会保障制度的最终确立。")]),t._v(" "),s("p",[t._v("二战以后，资本主义发展进入黄金时期国家干预增强，又由于美苏冷战，英国率先构建社会保障体系，此后，瑞典、挪威、芬兰、丹麦等北欧国家以及法国、德国、加拿大和澳大利亚等国都建成了福利国家，社会保障制度基本建立。这改善了广大人民群众的生活，缓和了社会矛盾，有利于经济发展。但是，过度的社会保障加重了国家财政负担，容易助长懒惰行为，降低劳动者的工作积极性。20世纪80年代，随着资本主义国家“滞胀”以及出于减少国家干预的目的，各国适当缩减了福利规模。")]),t._v(" "),s("p",[t._v("中华人民共和国成立后，1951年《中华人民共和国劳动保险条例》是新中国制定的第一部社会保险法规。随着改革开放的不断推进，我国的社会保障制度也日趋成熟，截至 2020年底，我国建成世界上规模最大的社会保障体系。日益健全的社会保障体系减轻了人们的后顾之忧，促进了国家社会经济的发展，也促进了社会公平，缓和了社会矛盾。在中国共产党成立一百周年（2021年）我国脱贫攻坚战全面胜利，区域性整体贫困得到解决，完成了消除绝对贫困的艰巨任务。")])])}),[],!1,null,null,null);s.default=a.exports}}]);