(window.webpackJsonp=window.webpackJsonp||[]).push([[22],{207:function(t,a,s){"use strict";s.r(a);var _=s(6),r=Object(_.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第三课-中国近代至当代政治制度的演变"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第三课-中国近代至当代政治制度的演变"}},[t._v("#")]),t._v(" 第三课 中国近代至当代政治制度的演变")]),t._v(" "),a("h2",{attrs:{id:"一、民国时期的政治制度"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、民国时期的政治制度"}},[t._v("#")]),t._v(" 一、民国时期的政治制度")]),t._v(" "),a("p",[t._v("1912年1月1日，南京临时政府正式成立，这标志着资产阶级共和制度在中国的诞生。新建立的中华民国采用五色旗为国旗，改用阳历，以中华民国纪年，1912年为民国元年。为防止袁世凯独裁，孙中山颁布了临时参议院制定的《中华民国临时约法》，以主权在民、平等自由为原则，确立行政、立法、司法三权分立的政治制度，规定实行责任内阁制，内阁总理由议会的多数党产生，总统颁布命令须由国务员副署才能生效。《中华民国临时约法》是中国历史上第一部具有资产阶级共和国宪法性质的重要文件，具有反对君主专制制度的进步意义。")]),t._v(" "),a("p",[t._v("辛亥革命后，各党派展开激烈竞争，开始了政党政治的尝试。1912 年 8月国民党成立后，宋教仁带领国民党在国会选举中获胜，宋教仁成为国会中多数党的领袖，将负责组织责任内阁。这威胁到了袁世凯的独裁统治。1913 年春，宋教仁在上海火车站被刺杀。“宋案”发生后，国民党发动了“二次革命”但很快被镇压下去。此后，政党政治名存实亡，大权独揽的袁世凯走上帝制之路。1915 年，袁世凯接受“劝进”当上了皇帝。随后在护国运动的影响下，袁世凯被迫取消帝制。袁世凯死后，北洋军阀内部纷争发展为军阀混战、割据的局面，政治格局混乱不堪。")]),t._v(" "),a("p",[t._v("1928年中国国民党成为中国新的统治者后，宣告训政时期开始，但国民党的“训政”就是剥夺人民权利的一党专政，还以种种借口一再拖延结束“训政”的时间。至 1948年，国民党才打出“行宪”的招牌，召开“国民大会”，选举蒋介石、李宗仁为总统、副总统。国民党政权作为大地主、大资产阶级的代表，企图以此为其独裁统治披上“宪政”的外衣，但最终逃脱不了崩溃的命运。")]),t._v(" "),a("div",{staticClass:"custom-block warning"},[a("p",{staticClass:"custom-block-title"},[t._v("中国宪政艰难的原因")]),t._v(" "),a("p",[t._v("①半殖民地半封建社会\n②列强侵略民族危机深重\n③军阀混战政局动荡\n④资本主义发展不充分，民族资产阶级力量弱小\n⑤封建思想根深蒂固")])]),t._v(" "),a("h2",{attrs:{id:"二、中国共产党在根据地和解放区的制度探索"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、中国共产党在根据地和解放区的制度探索"}},[t._v("#")]),t._v(" 二、中国共产党在根据地和解放区的制度探索")]),t._v(" "),a("h3",{attrs:{id:"_1-十年对峙土地革命时期-苏维埃政权"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#_1-十年对峙土地革命时期-苏维埃政权"}},[t._v("#")]),t._v(" (1)十年对峙土地革命时期——苏维埃政权")]),t._v(" "),a("p",[t._v("1930年夏，全国已经建立起大小十几块农村革命根据地，各级苏维埃政权也陆续建立起来。1931年，中华苏维埃第一次全国代表大会在江西瑞金召开。大会制定了宪法大纲，通过了土地法、劳动法等法令，宣布中华苏维埃共和国临时中央政府正式成立。宪法大纲规定，苏维埃政权属于工人、农民、红军士兵及一切劳苦民众。这是创建人民革命政权的尝试。（评价：不成熟）")]),t._v(" "),a("h3",{attrs:{id:"_2-国共合作全面抗战时期"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#_2-国共合作全面抗战时期"}},[t._v("#")]),t._v(" (2)国共合作全面抗战时期")]),t._v(" "),a("p",[t._v("1937年全面抗战爆发后，中国共产党领导的抗日民主根据地逐步扩大，发展为19个根据地。在各抗日根据地的政权建设上，中国共产党适应抗日民族统一战线的需要，设立边区政府，作为民国地方政府，推行抗日民主制度，委员由边区参议会选举产生。中国共产党通过“三三制”原则，与党外人士实行民主合作，共产党员只占三分之一，巩固和扩大了抗日民族统一战线。")]),t._v(" "),a("h3",{attrs:{id:"_3-解放战争时期"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#_3-解放战争时期"}},[t._v("#")]),t._v(" (3)解放战争时期")]),t._v(" "),a("p",[t._v("1945年，中共七大上毛泽东思想成为政权建设内容，《论联合政府》目标为建设新民主主义国家；1949年七届二中全会发表《论人民民主专政》。解放战争期间，中国共产党在解放区设置行政区以巩固新兴的人民政权。新中国成立前夕，毛泽东发表《论人民民主专政》，指出要“建立工人阶级领导的以工农联盟为基础的人民民主专政的国家”。")]),t._v(" "),a("h2",{attrs:{id:"三、中华人民共和国的政治制度"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#三、中华人民共和国的政治制度"}},[t._v("#")]),t._v(" 三、中华人民共和国的政治制度")]),t._v(" "),a("p",[t._v("中华人民共和国的根本政治制度是人民代表大会制度，基本政治制度是中国共产党领导的多党合作和政治协商制度、民族区域自治制度、基层群众自治制度。1949 年9月，中国人民政治协商会议第一届全体会议通过了具有临时宪法性质的《中国人民政治协商会议共同纲领》，人民代表大会制度初步确立。1954 年9月，第一届全国人民代表大会通过了《中华人民共和国宪法》，以国家根本大法的形式正式确定了我国实行人民代表大会制度，标志着人民代表大会制度正式建立起来，促进了我国社会主义工业化建设和社会主义改造的顺利进行。1957 年以后，由于受反右斗争扩大化和“左”倾、 “文化大革命”影响，人民代表大会制度更是遭受严重破坏和挫折。1982 年通过的《中华人民共和国宪法》进一步完善了人民代表大会制度，把直接选举人大代表的范围扩大到县一级，实行差额选举；固定任期，每届五年；赋予省级地方人大及其常委会制定地方性法规的权力。1982年宪法是一部具有民族特色和时代特色的社会主义宪法，为全面开创社会主义现代化建设新局面提供了坚实的法律基础。")]),t._v(" "),a("p",[t._v("1949 年9月，中国人民政治协商会议标志着中国共产党领导的多党合作和政治协商制度的确立。这一制度的基本内容是：长期共存、互相监督、肝胆相照、荣辱与共。1956年社会主义改造基本完成后，我国阶级状况发生深刻变化；改革开放后，中共中央进一步完善了十六字方针。“中国共产党领导的多党合作和政治协商制度将长期存在和发展”被写入宪法，走上了制度化轨道。")])])}),[],!1,null,null,null);a.default=r.exports}}]);