(window.webpackJsonp=window.webpackJsonp||[]).push([[23],{208:function(t,s,a){"use strict";a.r(s);var v=a(6),_=Object(v.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"第四课-中国历代变法与改革"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#第四课-中国历代变法与改革"}},[t._v("#")]),t._v(" 第四课 中国历代变法与改革")]),t._v(" "),s("h2",{attrs:{id:"一、中国古代重要变法与改革"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#一、中国古代重要变法与改革"}},[t._v("#")]),t._v(" 一、中国古代重要变法与改革")]),t._v(" "),s("p",[t._v("铁器的使用和牛耕的推广，使社会生产力得到进一步发展，生产关系处于急剧变动之中。井田制瓦解，私田大量出现，百家争鸣，兼并战争不断，各诸侯国为了适应社会变动，实现富国强兵，纷纷推行变法，以商鞅变法最为彻底。秦国废除井田制，奖励耕织，废除世卿世禄制，奖励军功，实行什伍连坐，建立县制等。这一系列措施打击了贵族特权，促进了封建政治、经济、军事的发展，使秦国成为政治军事强国，为秦统一奠定了基础。")]),t._v(" "),s("p",[t._v("两晋南北朝时期，内迁的游牧民族纷纷建立政权，相互之间征伐不断。新崛起的鲜卑族拓跋部建立北魏政权统一北方。在长期的冲突与交往中，民族交融成为历史发展的潮流。北魏孝文帝拓跋宏实行俸禄制，推行均田制、三长制等，有效地巩固了北魏政权，又采取迁都洛阳、易服装、改汉姓、说汉话、通婚姻等改革措施，加快了北方各族人民的交融，缓和了民族矛盾，缩小了南北差距，为中国统一多民族国家的发展作出了重要贡献。")]),t._v(" "),s("p",[t._v("北宋采取了一系列加强中央集权的措施，造成三冗两积。北宋中期，政治腐败，财政困难，各地农民起义不断，北部边境又经常受到游牧民族的袭扰。范仲淹主张改革腐败的官僚机构，但新法仅推行了一年多。王安石变法初期取得了显著成效，但阻力大，有些措施也欠妥当。用人不当加重了人民负担。明朝中后期，政治日益腐败，统治危机不断加深。张居正针对明中后期政治腐败、府库空虚、土地兼并严重、农民纷起反抗的状况，大力整肃吏治，加强官吏考核，裁减开支，清丈土地，改革税制。张居正辅政十年，国家财政收入增加，社会矛盾相对缓和，严重的封建统治危机得到暂时缓解。他死后只保留了一条鞭法。")]),t._v(" "),s("h2",{attrs:{id:"二、中国近代的改革探索"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#二、中国近代的改革探索"}},[t._v("#")]),t._v(" 二、中国近代的改革探索")]),t._v(" "),s("p",[t._v("鸦片战争后，一些爱国的有识之士求变求新，走上了探索救亡图存之路。以康有为、梁启超等为代表的一批维新志士宣传维新思想，主张学习日本、俄国变革政治，避免像波兰那样亡国的命运。1898 年6月光绪帝颁布变法诏令，一定程度上冲击了封建制度。但触犯了守旧势力的利益，遭到他们的激烈反对。9月，慈禧太后发动政变，废除了大部分变法措施。")]),t._v(" "),s("div",{staticClass:"custom-block danger"},[s("p",{staticClass:"custom-block-title"},[t._v("戊戌变法失败的原因")]),t._v(" "),s("p",[t._v("顽固势力强大；资产阶级维新派软弱妥协；脱离群众寄希望于无实权的皇帝；策略失误树敌过多急于求成")])]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("民族资本主义发展时间表")]),t._v(" "),s("p",[t._v("①产生：洋务运动后（19世纪60-70年代）")]),t._v(" "),s("p",[t._v("②初步发展：甲午中日战争战后清政府放宽了设厂限制")]),t._v(" "),s("p",[t._v("③黄金时期：1912-1937一战与南京国民政府")]),t._v(" "),s("p",[t._v("④卫所：1937-1949 侵略、战火、官僚资本主义挤压")]),t._v(" "),s("p",[t._v("⑤消失：1956")])]),t._v(" "),s("p",[t._v("戊戌变法的失败证明资产阶级改良道路在中国行不通。但其对封建思想文化进行了抨击，在社会上起到了思想启蒙作用，促进了中国人民的觉醒。1901 年初，清政府宣布实行“新政”，内容有改革教育、派遣留学生、编练新军、振兴商务、奖励实业等，客观上促进了中国民族资本主义的发展，但新政并没有使清政府摆脱内外困境，很快革命爆发了。中华民国建立后由于政局动荡，国家始终未能实现实质上的统一，这些改革多以失败告终。")]),t._v(" "),s("h2",{attrs:{id:"三、新中国成立以来的重要改革"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#三、新中国成立以来的重要改革"}},[t._v("#")]),t._v(" 三、新中国成立以来的重要改革")]),t._v(" "),s("p",[t._v("1949新政协；1949-1952年底，国民经济恢复；1950-1952土地改革；1953-1956三大改造；短时间内完成了社会主义革命，确立了社会主义基本制度，成功实现了中国历史上最深刻最伟大的社会变革。1978 年 12 月，中共作出把全党工作着重点转移到经济建设上来、实行改革开放的历史性决策。2012 年 11 月，中国共产党第十八次全国代表大会召开。大会确定了全面建成小康社会和全面深化改革开放，推进国家治理体系和治理能力现代化的总目标。2017 年 10 月，中国共产党第十九次全国代表大会召开，指出中国特色社会主义进入了新时代。中共十九大将全面深化改革总目标列为习近平新时代中国特色社会主义思想的重要内容并载入党章。")]),t._v(" "),s("p",[t._v("改革开放 40 多年来，从实行家庭联产承包、乡镇企业异军突起、取消农业税到农村承包地“三权”分置、打赢脱贫攻坚战、兴办经济特区、沿海沿边沿江沿线和内陆中心城市对外开放到加入世界贸易组织、共建“一带一路”、设立自由贸易试验区、成功举办中国国际进口博览会，从“引进来”到“走出去”，从搞好国营大中小企业、发展个体私营经济到深化国资国企改革、发展混合所有制经济。")])])}),[],!1,null,null,null);s.default=_.exports}}]);