(window.webpackJsonp=window.webpackJsonp||[]).push([[28],{213:function(t,a,s){"use strict";s.r(a);var v=s(6),_=Object(v.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第九课-近代西方的法律与教化"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第九课-近代西方的法律与教化"}},[t._v("#")]),t._v(" 第九课 近代西方的法律与教化")]),t._v(" "),a("h2",{attrs:{id:"一、近代西方法律制度的渊源与发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、近代西方法律制度的渊源与发展"}},[t._v("#")]),t._v(" 一、近代西方法律制度的渊源与发展")]),t._v(" "),a("p",[t._v("为了缓和平民和贵族的矛盾，公元前450年左右，罗马共和国颁布了《十二铜表法》。罗马帝国时期，法律制度更为完善（《万民法》）。6世纪，东罗马帝国皇帝查士丁尼下令颁布的《罗马民法大全》是古罗马法律的最高成就，也是近代西方法律制度的渊源。中古时期，日耳曼法作为庄园法庭审判的依据，教会制定和颁布了教会法。11世纪以后出现了研究和宣传罗马法的运动，促进了罗马法的传播。13世纪，英国通过《大宪章》，确立了法律至上和王权有限的原则。光荣革命后，英国确立了君主立宪制，法律体系更加完善，美国等很多国家在学习英国法律的基础上制定了本国法律，他们构成了普通法系，也称英美法系。")]),t._v(" "),a("p",[t._v("法国建立在罗马法基础上的法律体系日益成熟，1789年，法国爆发大革命，受启蒙思想和大革命的影响制定了一系列法律。1804年，拿破仑颁布《法国民法典》，拿破仑法典与此前颁布的法律构成了法国的成文法体系，后来逐渐形成了以罗马法为基础，以法国民法典为代表的世界性法律体系，称为大陆法系或民法系。")]),t._v(" "),a("div",{staticClass:"custom-block warning"},[a("p",{staticClass:"custom-block-title"},[t._v("英美法系与大陆法系的异同点")]),t._v(" "),a("p",[t._v("相同点：本质都是近代西方资产阶级法律体系；启蒙思想影响；背景都是资产阶级民族统一国家建立；都是对罗马法的继承。")]),t._v(" "),a("p",[t._v("不同点：")]),t._v(" "),a("p",[t._v("英美法系以判例法为主，遵循先例，法官地位突出，可创立先例也可以解释旧有的先例。")]),t._v(" "),a("p",[t._v("大陆法系以成文法为主，遵循宪法的根本法地位，明确立法司法分工，法官作用不突出。")])]),t._v(" "),a("h2",{attrs:{id:"二、近代西方法律制度的基本特征"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、近代西方法律制度的基本特征"}},[t._v("#")]),t._v(" 二、近代西方法律制度的基本特征")]),t._v(" "),a("p",[t._v("在国家权力结构层面上，坚持权力制衡、三权分立，国家权力分为立法权、行政权和司法权，在法律内容上，注重保护个人权利，包括生命权、自由权和财产权等，坚持程序公正和无罪推定，从民众中产生陪审团，使民众能够直接参与法律事务。无罪推定原则指的是所有被审判者在判决之前都被视为无罪。")]),t._v(" "),a("p",[t._v("西方法律制度局限性是，确认了私有财产制度，财产的多少往往决定了法律地位的高低，同时对个人权利的认定也有逐渐改进的过程，直到20世纪，黑人、原住民和妇女还在积极斗争。")]),t._v(" "),a("h2",{attrs:{id:"三、宗教伦理与教化"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#三、宗教伦理与教化"}},[t._v("#")]),t._v(" 三、宗教伦理与教化")]),t._v(" "),a("p",[t._v("基督教影响了中古时期欧洲人的政治、经济和社会生活的各个方面，教士们搜集和抄录经典，保存了一些宝贵的古典文化，但他们更重要的任务是宣讲教义。他们还开办学校，主要是宗教学校也有世俗学校，主要讲述宗教内容。基督教的宗教伦理和教化作用强化了教会对人们的控制，深刻影响了人们的思想意识和日常行为。")]),t._v(" "),a("p",[t._v("宗教改革后，新教适应了原始积累时期新兴资产阶级的政治、经济诉求，但新教仍然束缚人们的行为，麻醉人们的思想。新教还排斥其他教派，引起了多次宗教冲突，一些对教义持有不同意见的人被斥为“异端”，遭到迫害。")])])}),[],!1,null,null,null);a.default=_.exports}}]);