(window.webpackJsonp=window.webpackJsonp||[]).push([[30],{215:function(t,s,a){"use strict";a.r(s);var v=a(6),_=Object(v.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"第八课-中国古代的法治与教化"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#第八课-中国古代的法治与教化"}},[t._v("#")]),t._v(" 第八课 中国古代的法治与教化")]),t._v(" "),s("h2",{attrs:{id:"一、先秦时期的德治与法治"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#一、先秦时期的德治与法治"}},[t._v("#")]),t._v(" 一、先秦时期的德治与法治")]),t._v(" "),s("p",[t._v("夏商时期，君王及奴隶主贵族可以随意残害奴隶。西周统治者为维护社会等级秩序，建立了以宗法为核心的礼制，同时提出“敬天保民”的思想，有一定的进步性。中国最早的成文法出现在春秋时期。郑国的子产“铸刑书”，把刑法浇铸在金属器皿上，制定了中国历史上最早的成文法。子产“铸刑书”引发了一场辩论，这就是早期的德治与法治之争。")]),t._v(" "),s("p",[t._v("春秋战国时期，德治与法治之争在思想界体现为儒家与法家之争。儒家认为人性善，主张德治，代表人物是孔子和孟子。孔子提出，统治者要“为政以德”“节用而爱人，使民以时”，不要过度消耗民力。孟子建议统治者“施仁政于民，省刑罚，薄税敛”。法家认为人性恶，主张法治，代表人物是商鞅与韩非。商鞅在秦国颁行了一系列法令，保护新兴地主阶级的权益。韩非提倡君主不要谈礼义，而要以法、术、势驾驭臣下；君主赏罚分明，则臣民必能守法奉令。为保证法令畅通，韩非主张“以法为教”“以吏为师”。儒家主张通过道德礼义教化民众，重视民生与民意，但儒家思想并不适用于兼并战争激烈的战国时期。法家思想既能带来富国强兵的现实利益，又满足了各国君主专制的愿望。")]),t._v(" "),s("h2",{attrs:{id:"二、秦汉至隋唐时期的法律与教化"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#二、秦汉至隋唐时期的法律与教化"}},[t._v("#")]),t._v(" 二、秦汉至隋唐时期的法律与教化")]),t._v(" "),s("p",[t._v("秦以法家思想治国，历朝法典多以“律”命名。秦汉朝廷还发布法律文告，称“令”。律和令都具有法律效力。魏晋时期，律令儒家化是最重要的变化，主要是因为汉武帝以后儒家思想成为主流思想，儒家知识分子以经注律。此后，法律以亲属之间的尊卑亲疏作为量刑的重要原则之一，目的在于维护儒家提倡的三纲五常。律在唐初经过多次删繁就简。《唐律疏议》继承了汉魏以来法律制定和阐释的经验，是中国现存最早、最为完整的封建法典，是中华法系确立的标志。唐律是礼法结合的典范，732 年，唐朝政府颁行《大唐开元礼》，是一部体系庞大、体例严谨、内容繁复的礼仪法典，也是秦汉以来封建礼仪制度的集大成。在社会层面，唐朝政府推广魏晋南北朝以来重视家训的经验，强化基层教化。")]),t._v(" "),s("h2",{attrs:{id:"三、宋元至明清时期的法律与教化"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#三、宋元至明清时期的法律与教化"}},[t._v("#")]),t._v(" 三、宋元至明清时期的法律与教化")]),t._v(" "),s("p",[t._v("宋朝基本沿用唐朝法律体系，元朝在司法实践中仍广泛援引唐律。明朝在司法实践中又特别重视“例”，开创了律例合编的体例。清朝法典同样非常重视例。宋朝儒学开始向基层渗透，并发展出理学。程朱理学在南宋后期逐步确立统治地位，控制教育与科举，并通过授徒、书院讲学等方式在社会上广泛传播，甚至深入族规、家训之中。儒学士人以乡约教化乡里，明朝后期改为宣讲“六谕”，使乡约逐渐带有强制力。不遵乡约的百姓要受到处罚，甚至送官府治罪。原本由儒学士人发起的教化百姓的乡约，经政府利用和推广而具有约束力，并与法律合流。")]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("乡约发挥的作用")]),t._v(" "),s("p",[t._v("①利于维护社会秩序与道德教化")]),t._v(" "),s("p",[t._v("②加强了基层社会治理")]),t._v(" "),s("p",[t._v("③有利于发展生产发展经济")]),t._v(" "),s("p",[t._v("④促进儒学文化传统道德传播")])]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("乡约宣讲内容的变化")]),t._v(" "),s("p",[t._v("①推动力量：民间自发到地方官吏推动")]),t._v(" "),s("p",[t._v("②内容：道德教化到教谕守法顺从")]),t._v(" "),s("p",[t._v("③作用：教化手段变为强制力统治工具")]),t._v(" "),s("p",[t._v("④逐步与法律合流")])])])}),[],!1,null,null,null);s.default=_.exports}}]);