(window.webpackJsonp=window.webpackJsonp||[]).push([[35],{220:function(t,s,v){"use strict";v.r(s);var a=v(6),_=Object(a.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"第十四课-历史上的疫病与医学成就"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#第十四课-历史上的疫病与医学成就"}},[t._v("#")]),t._v(" 第十四课 历史上的疫病与医学成就")]),t._v(" "),s("h2",{attrs:{id:"一、历史上的疫病"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#一、历史上的疫病"}},[t._v("#")]),t._v(" 一、历史上的疫病")]),t._v(" "),s("p",[t._v("14世纪时，欧洲鼠疫大流行，又被称为“黑死病”，这是文艺复兴的直接原因。黑死病流行导致欧洲人口死亡过半，劳动力短缺，生产停滞，欧洲许多地区发生严重饥荒。疫病给人们的健康带来巨大威胁，甚至导致政治、经济的变动，冲击了旧的思想秩序，促进了新思想的萌芽，客观上推动了医学的进步。")]),t._v(" "),s("p",[t._v("古人在措施上重视公共卫生，古罗马非常注意公共卫生，把修建饮水道、下水道当作重要工程。中国古代不少地方官组织清理污水、疏通井渠河道；在大灾之后，及时掩埋尸体，发放药物。东晋葛洪所著《肘后备急方》记载了青蒿对疟疾的治疗作用。中国古人为了防治天花，发明了人痘接种。人痘接种法在明朝中期已经广泛使用，后来传到欧洲。")]),t._v(" "),s("p",[t._v("古人已经建立疫病的救治机构。西汉后期，政府对疫病患者进行隔离，隋唐时期，寺庙病坊也收治病人。古罗马时期的医院也收治疫病患者。近代医学的进步，有效控制了很多疫病的传播。18世纪晚期，英国医生詹纳发明牛痘接种法，使天花的发病率大大降低。19 世纪，法国微生物学家巴斯德创立巴氏消毒法，1928 年英国细菌学家弗莱明发现了青霉素，改变了人类对细菌传染束手无策的状态。")]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("古人值得今天借鉴的疫病防治方法")]),t._v(" "),s("p",[t._v("隔离、疫苗接种、清理污水重视公共卫生、掩埋尸体、发放药物、修建救治机构")])]),t._v(" "),s("h2",{attrs:{id:"二、中医药的成就"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#二、中医药的成就"}},[t._v("#")]),t._v(" 二、中医药的成就")]),t._v(" "),s("p",[t._v("战国时的扁鹊已经用望、闻、问、切四诊法进行诊断。成书于战国至西汉间的《黄帝内经》，以阴阳五行学说解释人体的生理现象和病理变化，阐明人与自然的有机联系，是一部重要的中医基础理论著作。东汉晚期，名医华佗精通外科手术，创制了麻醉药“麻沸散”以及健身方法“五禽戏”。医学家张仲景提出辨证施治的原则，著成《伤寒杂病论》，奠定了中医临床学的基础。唐朝医学家孙思邈著成临床百科全书《千金方》。他被后人尊称为“药王”。")]),t._v(" "),s("p",[t._v("针灸在中医治疗中发挥很大作用。它建立在经络学说基础上，治疗效果显著，简便经济，北宋时期出现了医学教学模型——针灸铜人。唐朝的《唐本草》，是世界上第一部由政府颁布的药典。明朝李时珍总结千百年来中药学的经验，著成《本草纲目》。这部著作集中药学知识之大成，被誉为“东方药学巨典”。（《本草纲目》的体例特点：体例完备、图文并茂、记述详尽、分类先进）")]),t._v(" "),s("p",[t._v("中华人民共和国成立后，在中西医结合方针的指导下，全国广泛设立中医院与中医院校，中医药工作者不断整理中医典籍，开发中成药制剂。屠呦呦受葛洪《肘后备急方》启发从青蒿中分离出能够有效抵抗疟疾的青蒿素，对人类防治疟疾发挥了巨大作用。2015 年，屠呦呦获得诺贝尔生理学或医学奖。")]),t._v(" "),s("h2",{attrs:{id:"三、西医在中国的传播"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#三、西医在中国的传播"}},[t._v("#")]),t._v(" 三、西医在中国的传播")]),t._v(" "),s("p",[t._v("西医以化学、生物学、物理学等实验科学为依托，借助精密仪器及数据分析取得巨大成就。17 世纪，英国生理学家威廉·哈维著成《心血运动论》，揭示了血液循环的规律；荷兰人列文虎克通过手工磨制的显微镜，对肉眼不可见的微生物进行了细致观察，19 世纪创立了实验药理学。")]),t._v(" "),s("p",[t._v("明末清初，西医传入中国，但影响有限。鸦片战争后，西式医院在通商口岸建立起来。牛痘接种法、麻醉术、放射技术以及其他近代西医成就陆续被引入中国。随后，西医院校也建立起来，对人们的健康起到了积极的作用。中国的一些大城市借鉴了西方的公共卫生措施。")]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("西医在华传播的影响")]),t._v(" "),s("p",[t._v("有利于中国医学的近代化、改善公共卫生环境，革除陋习；有利于西方先进思想传入，启迪民智；")]),t._v(" "),s("p",[t._v("有利于中医自我革新与科学化；但也造成了文化侵略，过于否定中医，不利于中华文明的传承。")])]),t._v(" "),s("div",{staticClass:"custom-block danger"},[s("p",{staticClass:"custom-block-title"},[t._v("中西医理念的异同比较")]),t._v(" "),s("p",[t._v("起源与理论基础：中医理论起源于阴阳五行等，发展取决于前人的总结经验；西医形成发展依赖于药物化学、分子、细胞、生物学的发展和科学的进步。")]),t._v(" "),s("p",[t._v("研究疾病的观念：中医是整体观念和辨证治疗，治疗疾病注重经验；西医注重对症治疗。")]),t._v(" "),s("p",[t._v("诊断方法的不同：中医讲究望闻问切；西医借助医疗器械诊断。")]),t._v(" "),s("p",[t._v("治疗方法的不同：中医通过中药各种剂型和针灸等手段治疗；西医通过手术或化学药物对疾病治疗。")]),t._v(" "),s("p",[t._v("优势不同：中医优势在于治疗慢性病、疑难杂症及功能性疾病；西医优势在于治疗急性病和器质性疾病。")])])])}),[],!1,null,null,null);s.default=_.exports}}]);