(window.webpackJsonp=window.webpackJsonp||[]).push([[41],{226:function(t,a,s){"use strict";s.r(a);var r=s(6),e=Object(r.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第五课-工业革命与工厂制度"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第五课-工业革命与工厂制度"}},[t._v("#")]),t._v(" 第五课 工业革命与工厂制度")]),t._v(" "),a("h2",{attrs:{id:"一、机器大生产与工厂制度"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、机器大生产与工厂制度"}},[t._v("#")]),t._v(" 一、机器大生产与工厂制度")]),t._v(" "),a("p",[t._v("以改良蒸汽机为代表，引发了从手工劳动到机器大生产的巨大变革。工厂出现使大批产业工人被工厂主聚集起来，在专门化的厂房中劳作，彻底改变了手工作坊零碎分散的生产状态。为保证机器的昼夜运行，工人往往实行倒班制。工厂主制定了严格的规章制度，强化纪律意识，生产流水线被广泛应用到生产过程中，各生产环节协同劳作，分工精细。原料由工厂统一供应、合理调配。工厂制度有利于科学管理、提高生产效率、挖掘工人的劳动潜质，从而产生更大的经济效益。19世纪中后期，清朝洋务派创办了江南制造总局、福州船政局等一系列近代企业，引进了西方的工厂制度，进行机器大生产。后来，张謇、范旭东等一批民族资本家主张实业救国，也开办工厂并借鉴西方工厂的管理经验，中国民族工业初步发展起来。")]),t._v(" "),a("h2",{attrs:{id:"二、工业革命后生活方式的变化"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、工业革命后生活方式的变化"}},[t._v("#")]),t._v(" 二、工业革命后生活方式的变化")]),t._v(" "),a("p",[t._v("工业革命促进了城市化的发展，也改变了人们的生活空间。交通运输业的进步，便利了人们的出行。运河的开凿和铁路运输的发展大大增加了社会的流动性。工业革命也促进了乡村的改变。农业机械日益普及，普遍建立了大农场，农业现代化水平大大提高。大量人口走出，人们的眼界开阔了。人们的时间观念更为增强。工厂制度及蒸汽机车等交通工具出现以后，准时准点成为现代生活的准则。西方国家不断通过立法推行初等教育的同时，20世纪初清政府推行“癸卯学制”，中国出现了大量小学堂和中学堂，这对普及初等教育起到重要作用。")]),t._v(" "),a("p",[t._v("工业革命也给民众的生活带来消极影响。工人劳动时间过长，工作与生活环境恶劣，传染病与职业病严重危害产业工人的健康。19世纪，欧洲社会主义运动风起云涌，工人运动标志着工人阶级登上历史舞台，促进了马克思主义的诞生。")])])}),[],!1,null,null,null);a.default=e.exports}}]);