(window.webpackJsonp=window.webpackJsonp||[]).push([[45],{230:function(t,a,s){"use strict";s.r(a);var r=s(6),e=Object(r.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第七课-古代的商业贸易"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第七课-古代的商业贸易"}},[t._v("#")]),t._v(" 第七课 古代的商业贸易")]),t._v(" "),a("h2",{attrs:{id:"一、商业贸易的起源与发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、商业贸易的起源与发展"}},[t._v("#")]),t._v(" 一、商业贸易的起源与发展")]),t._v(" "),a("p",[t._v("原始社会后期，随着社会分工、产品剩余及私有制的产生，出现了交换，在此基础上逐步发展起商业贸易。商业贸易的根本原因是生产力的发展。在古代中国，交换物品的场所称作“市”，商朝就已经出现了专门从事商品交换的商人，商业贸易遍及商朝统治区域且主要掌握在官府和贵族手里。春秋战国时期，“工商食官”的格局被突破，民间出现商业手工业。秦汉时期，货币、车轨、度量衡的统一，促进了全国的商品流通。从隋唐到两宋，随着商品经济的发展，城市坊市分区制度逐步瓦解，重农抑商被强化。元明清时期，纸币交子出现；娱乐场所瓦子出现，街市草市的出现打破了市场的空间限制，早市夜市的出现打破了时间限制，商税是政府税收的重要组成。农产品和手工业产品的商品化程度不断加深，形成全国范围的商业贸易网络，地域性的商人群体——商帮兴盛。")]),t._v(" "),a("p",[t._v("古埃及商业的历史十分悠久，那时的对外贸易控制在国家手中，古希腊各城邦形成了若干个商业贸易中心，古罗马海外贸易航线四通八达，拜占庭帝国一度垄断了中国丝绸等东方奢侈品在欧洲市场的贸易。通过丝绸之路，商人们将中国生产的丝绸等运往中亚、西亚、欧洲和北非，商船从东南沿海各港口出发，近达南洋各地，远达波斯湾、阿拉伯海和红海沿岸地区。官府在东南沿海一带设置市舶司，掌管对外贸易。朝贡贸易是中国古代对外贸易的重要方式之一。清政府在广州设置公行，统揽对外贸易事务。商业贸易的发展促进了不同地区、不同国家之间的交流，大大丰富了人们的物质生活和精神生活。")]),t._v(" "),a("h2",{attrs:{id:"二、货币、信贷与商业契约"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、货币、信贷与商业契约"}},[t._v("#")]),t._v(" 二、货币、信贷与商业契约")]),t._v(" "),a("p",[t._v("公元前11世纪，古埃及开始用铜块和银块作为货币。公元前8—前7世纪，小亚细亚出现了金属铸币。公元前22—前21世纪，两河流域出现了经营借贷的商人，神庙、宫廷也从事放贷业务，还包括具有社会救济性质的免息借贷。公元前16—前 11 世纪，古埃及出现了较为完备的借贷合约。借贷行为必须有证人，还要履行担保等程序，受到法律保护。公元前4世纪，古希腊专门从事货币兑换、保管和汇兑的货币经营行业开始兴起，出现了汇票的雏形。")]),t._v(" "),a("p",[t._v("中国古代《周礼》中已经有了借贷纠纷的记载。春秋时期，实物借贷形式已经比较普遍，并出现了货币信贷。唐朝的信贷业务，由存贷款发展到汇兑，出现了类似现代汇票的“飞钱”。明朝中期以后，钱铺等新式金融机构盛行起来。到了清朝，资本性借贷也有显著发展，出现了本票性质的庄票。")])])}),[],!1,null,null,null);a.default=e.exports}}]);