(window.webpackJsonp=window.webpackJsonp||[]).push([[48],{233:function(t,s,v){"use strict";v.r(s);var a=v(6),_=Object(a.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"第十一课-古代战争与地域文化的演变"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#第十一课-古代战争与地域文化的演变"}},[t._v("#")]),t._v(" 第十一课 古代战争与地域文化的演变")]),t._v(" "),s("h2",{attrs:{id:"一、亚历山大远征与希腊化时代"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#一、亚历山大远征与希腊化时代"}},[t._v("#")]),t._v(" 一、亚历山大远征与希腊化时代")]),t._v(" "),s("p",[t._v("公元前334 年，马其顿国王亚历山大率军入侵波斯，征服了从小亚细亚到印度河流域的广大地区。战争给被征服地区造成了严重破坏，固有的文化发展进程在一定程度上被打断。亚历山大统治期间，试图以希腊文化为主导，融合埃及和西亚文化。在埃及，他自称法老之子。在波斯，沿用波斯帝国的行省制度。公元前323 年，亚历山大帝国分裂为托勒密埃及、塞琉古王国和马其顿王国三个主要国家。这些区域被统称为“希腊化世界”，从亚历山大远征到罗马最终征服托勒密埃及之间大约300年被称为“希腊化时代”。")]),t._v(" "),s("p",[t._v("（希腊化的表现）希腊人和马其顿人逐渐融合，成为希腊化各国的统治阶级；被征服地区原有的上层人士也不同程度地接受了希腊文化；希腊文化的中心逐渐东移，亚历山大城、安条克和帕加马等成为新的希腊文化中心；促进了文学、科学等许多领域的发展，如《荷马史诗》、《几何原本》以及阿基米德的定理。")]),t._v(" "),s("p",[t._v("西亚、北非的文化仍在希腊化世界保持长期影响。在广大的乡村地区，农民仍如过去一样，说着他们祖先的语言，保持他们原有的宗教和风俗，但民族压迫与民族矛盾仍然存在。")]),t._v(" "),s("h2",{attrs:{id:"二、蒙古西征与东西方交流"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#二、蒙古西征与东西方交流"}},[t._v("#")]),t._v(" 二、蒙古西征与东西方交流")]),t._v(" "),s("p",[t._v("成吉思汗以复仇为由，发动第一次西征，灭掉花剌子模国，蒙古军队直抵黑海北岸。蒙古第二次西征时征服今俄罗斯、乌克兰地区，一直打到今德国、匈牙利和巴尔干半岛。13世纪中叶，蒙古以西亚地区为目标，进行第三次西征，攻占巴格达。蒙古在其他统治区形成了四大汗国，它们在名义上仍将元朝尊为宗主国。")]),t._v(" "),s("p",[t._v("蒙古的三次西征，给被征服地区的社会经济带来严重破坏，但客观上推动了东西方的交流。受教皇和法国国王派遣的两位欧洲传教士，意大利旅行家马可·波罗和畏兀儿人列班·扫马对东西方交流做出了贡献。")]),t._v(" "),s("div",{staticClass:"custom-block warning"},[s("p",{staticClass:"custom-block-title"},[t._v("推动东西方交流的原因")]),t._v(" "),s("p",[t._v("①发达的国内外交通；")]),t._v(" "),s("p",[t._v("②欧亚大陆因此连为一体；")]),t._v(" "),s("p",[t._v("③重视商贸；")]),t._v(" "),s("p",[t._v("④宗教政策开放；")]),t._v(" "),s("p",[t._v("⑤历史人物的贡献")])]),t._v(" "),s("p",[t._v("蒙古西征还引发了较大规模的民族迁徙。由东向西迁徙的移民主要是西征的蒙古军队。大批移民自西东迁，进入中国中原地区，被称为“色目人”，意即各色名目的人。他们中的大部分后来融入汉族和蒙古族，产生了回族。")])])}),[],!1,null,null,null);s.default=_.exports}}]);