(window.webpackJsonp=window.webpackJsonp||[]).push([[53],{238:function(t,a,s){"use strict";s.r(a);var v=s(6),_=Object(v.a)({},(function(){var t=this,a=t._self._c;return a("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[a("h1",{attrs:{id:"第十四课-文化传承的多种载体与发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#第十四课-文化传承的多种载体与发展"}},[t._v("#")]),t._v(" 第十四课 文化传承的多种载体与发展")]),t._v(" "),a("h2",{attrs:{id:"一、学校教育的发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#一、学校教育的发展"}},[t._v("#")]),t._v(" 一、学校教育的发展")]),t._v(" "),a("p",[t._v("中国古代学校教育的主要形式是官学与私学，私人设立的学塾、村学和蒙学构成基层社会教育的重要形式。书院制度的建立，更推动了私学的发展。宋代的白鹿洞书院最为著名。京师大学堂创建于戊戌维新运动中，既是近代中国第一所由中央政府建立的综合大学，又是国家最高教育行政机关，采取分科教育的方式，是中国教育近代化的标志。1912年，京师大学堂改名为“北京大学”。1916年蔡元培接任北京大学校长后，北京大学成为新文化运动的中心和学术研究的重镇。1911年创办的清华学堂，次年更名为“清华学校”，1928年组建为国立清华大学。新中国成立后，国家收回了教育主权，决定教育要为国家建设服务，学校要向广大工农开门，大力提高人民的文化水平，提出了受教育者要在德、智、体等方面全面发展的教育方针，逐步形成比较完整的国民教育体系。“文化大革命”结束后，1977年中断十年的高考制度恢复，1983 年，邓小平提出“教育要面向现代化，面向世界，面向未来”的指导方针。20世纪90年代，国家开始实行“科教兴国”发展战略，大量增加教育投入。")]),t._v(" "),a("div",{staticClass:"custom-block danger"},[a("p",{staticClass:"custom-block-title"},[t._v("各时期留学教育的表现与代表人物")]),t._v(" "),a("p",[t._v("①洋务运动：官派留学学习技术，代表人物：詹天佑")]),t._v(" "),a("p",[t._v("②19世纪末：甲午战争留日风潮，代表人物：陈独秀")]),t._v(" "),a("p",[t._v("③清末民初：庚款留学赴美深造，代表人物：胡适")]),t._v(" "),a("p",[t._v("④一战时期：勤工俭学留法热潮，代表人物：邓小平")]),t._v(" "),a("p",[t._v("⑤五四运动：十月革命以俄为师，代表人物：聂荣臻")]),t._v(" "),a("p",[t._v("影响："),a("strong",[t._v("有利于华夏文化传播与中外交流，为近代化事业培养了优秀人才与中坚力量，促进了近代的思想解放与教育发展。")])])]),t._v(" "),a("p",[t._v("古希腊产生了欧洲最初的学校形式，奠定了分科教育的基础。法国拿破仑建立的“大学区”制度保留至今。德国人洪堡秉持“研究教学合一”的精神创办了柏林大学，美国大学则逐渐倾向于世俗化与商业化，使更多的人有机会接受大学教育。\n学校教育具有重要意义。在文化传承、科学研究、人才培养等方面，学校教育发挥了举足轻重的作用，促进了文化典籍的保存与传播，推动了科技的发展，提高了国民的文化素质。")]),t._v(" "),a("h2",{attrs:{id:"二、印刷书的诞生"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#二、印刷书的诞生"}},[t._v("#")]),t._v(" 二、印刷书的诞生")]),t._v(" "),a("p",[t._v("约在公元前30世纪，埃及出现的纸草书卷，一般被认为是书籍的雏形。中国早期的书籍是用竹片或木板作书写材料的简策和用缣帛作书写材料的帛书，盛行于春秋战国乃至秦汉时期。公元前2世纪，中国已出现用植物纤维制成的纸。105年，东汉蔡伦改进制成“蔡侯纸”。唐朝已有雕版印刷品。北宋时的毕昇发明用胶泥制的活字。约15世纪中叶，德国人谷登堡采用金属活字印刷，直接催生了被视作“新一代的书籍”的印刷书。")]),t._v(" "),a("p",[t._v("印刷书的诞生有助于文化的大众化，对提高人们的文化修养、促进各民族的发展，具有重要意义。它推动了文化的传承与传播，促进了思想解放，有利于文化交流。")]),t._v(" "),a("h2",{attrs:{id:"三、图书馆的成长"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#三、图书馆的成长"}},[t._v("#")]),t._v(" 三、图书馆的成长")]),t._v(" "),a("p",[t._v("图书馆担负保存人类文化典籍的职责，古文明遗址中保存最完整、规模最宏大、书籍最齐全的图书馆，是公元前7世纪的亚述巴尼拔王建立的，这里收藏有约25 000块泥版文书。19世纪下半叶，英、美等国家出现了面向所有人开放的具有近代意义的公共图书馆。中国古代朝廷设有专门掌管典籍的史官，并建有“府”“阁”“堂”“室”等藏书之所。明清两代出现了一批著名的藏书家和藏书楼。明朝中期建造的天一阁，是古代中国私家藏书的代表。1909 年，清政府开始筹建京师图书馆，国家图书馆的职能开始体现。中华人民共和国成立后，京师图书馆改名为“北京图书馆”，1998年改称“国家图书馆”。")]),t._v(" "),a("h2",{attrs:{id:"四、博物馆的建设与发展"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#四、博物馆的建设与发展"}},[t._v("#")]),t._v(" 四、博物馆的建设与发展")]),t._v(" "),a("p",[t._v("公元前290年前后，埃及亚历山大城的缪斯宫后来被称为“亚历山大博物馆”，一般认为这是人类历史上最早的博物馆。1683 年，牛津大学建立的阿什莫林博物馆，被视作第一个具有近代特征的博物馆。到18世纪，大英博物馆、法国的卢浮宫等重要的博物馆，都建立于这一时期。19 世纪六七十年代，法国人韩伯禄在上海建立的自然历史博物院，是中国最早出现的近代意义上的博物馆。中国人自建的第一个公共博物馆，是1905年实业家张謇在江苏南通建立的南通博物苑。中华民国成立以后，1926 年，国立历史博物馆正式开馆。1933 年，蔡元培等倡议在南京建立国立中央博物院。故宫博物院位于北京故宫紫禁城内，成立于1925年10月10日，是中国最大的古代艺术博物馆，也是世界著名的博物馆。1959年7月中国人民革命军事博物馆建成，2003年2月，在中国历史博物馆、中国革命博物馆基础上，中国国家博物馆正式组建。")]),t._v(" "),a("p",[t._v("博物馆是征集、收藏、展示、研究自然和人类文化遗产的重要场所，也是进行国民教育的重要场所，全方位向公众提供各领域的知识，担负着文化传承和传播的使命。")])])}),[],!1,null,null,null);a.default=_.exports}}]);